contracts/Gap.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {IEAS, AttestationRequest, AttestationRequestData, MultiAttestationRequest} from "@ethereum-attestation-service/eas-contracts/contracts/IEAS.sol";

contract Gap is Initializable, OwnableUpgradeable {
    IEAS public eas;

    struct AttestationRequestNode {
        bytes32 uid;
        MultiAttestationRequest multiRequest;
        AttestationRequestNode[] requestNodes;
    }

    function initialize(address easAddr) public initializer {
        eas = IEAS(easAddr);
        __Ownable_init();
    }

    function multiSequentialAttest(AttestationRequestNode calldata requestNode) private {
        _multiSequentialAttest(requestNode, 0x0);
    }

    function _multiSequentialAttest(AttestationRequestNode calldata requestNode, bytes32 uid) private {

        MultiAttestationRequest[] memory requests = new MultiAttestationRequest[](1);
        requests[0] = requestNode.multiRequest;

        if (uid != 0x0) {
            for(uint256 j = 0; j < requests[0].data.length; j++) {
                AttestationRequestData memory data = requests[0].data[j];
                data.refUID = uid;
                requests[0].data[j] = data;
            }
        }

        bytes32[] memory totalUids = eas.multiAttest(requests);

        for(uint256 i = 0; i < requestNode.requestNodes.length; i++) {
            _multiSequentialAttest(requestNode.requestNodes[i], totalUids[0]);
        }
    }

/*

    function multiSequentialAttest(MultiAttestationRequest[] calldata multiRequests) external {
        bytes32[][] memory totalUids = new bytes32[][](multiRequests.length);

        for (uint256 i = 0; i < multiRequests.length; i++) {
            MultiAttestationRequest[] memory requests = new MultiAttestationRequest[](1);
            requests[0] = multiRequests[i];
            if (i > 0) {
                for(uint256 j = 0; j < requests[0].data.length; j++) {
                    AttestationRequestData memory data = requests[0].data[j];
                    data.refUID = totalUids[i-1][0];
                    requests[0].data[j] = data;
                }
            }
            totalUids[i] = eas.multiAttest(requests);
        }
    }
    */
}

contracts/GapResolver.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import {SchemaResolver} from "@ethereum-attestation-service/eas-contracts/contracts/resolver/SchemaResolver.sol";
import {IEAS} from "@ethereum-attestation-service/eas-contracts/contracts/IEAS.sol";
import {Attestation} from "@ethereum-attestation-service/eas-contracts/contracts/Common.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

contract GAPResolver is SchemaResolver, Initializable, OwnableUpgradeable {

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor(IEAS eas) SchemaResolver(eas) {
      _disableInitializers();
    }

    function onAttest(
        Attestation calldata attestation,
        uint256 /*value*/
    ) internal view override returns (bool) {
        return true;
    }

    function onRevoke(
        Attestation calldata attestation,
        uint256 /*value*/
    ) internal view override returns (bool) {
        return true;
    }

}

